import { Note } from "../types";

const API_BASE = "http://localhost:5123"; // <-- change to your backend

async function throwIfBad(res: Response, label: string) {
  if (!res.ok) {
    const text = await res.text().catch(() => "");
    throw new Error(`${label} failed (${res.status}) ${text}`);
  }
}

export const NotesApi = {
  async getNotes(): Promise<Note[]> {
    const res = await fetch(`${API_BASE}/api/notes`);
    await throwIfBad(res, "GET /api/notes");
    return (await res.json()) as Note[];
  },

  async putNotes(notes: Note[]): Promise<void> {
    const res = await fetch(`${API_BASE}/api/notes`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(notes)
    });
    await throwIfBad(res, "PUT /api/notes");
  }
};
